import React, { useState, useEffect } from 'react';
import './HomePage.css';

const HomePage = () => {
  const [location, setLocation] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [humidity, setHumidity] = useState(0);

  // Function to fetch weather data for the selected location
  const fetchWeatherData = (location) => {
    // Fetch data logic (replace with actual API call or data fetching method)
    const mockData = {
      temperature: 20,
      location: 'Melbourne',
      warning: "High rainfall expected, take precautions!",
      accidentWarning: "High accident risk due to weather conditions!",
      forecast: [
        { minTemp: 11, maxTemp: 20, humidity: 66, wind: 20, rainfall: 66, accidents: 2 },
        { minTemp: 11, maxTemp: 19, humidity: 71, wind: 19, rainfall: 71, accidents: 3 },
        { minTemp: 12, maxTemp: 22, humidity: 74, wind: 19, rainfall: 74, accidents: 3 },
        { minTemp: 13, maxTemp: 21, humidity: 75, wind: 20, rainfall: 75, accidents: 3 },
        { minTemp: 14, maxTemp: 23, humidity: 65, wind: 18, rainfall: 60, accidents: 2 }
      ]
    };
    setWeatherData(mockData);
    setHumidity(mockData.forecast[0].humidity);  // Example to set initial humidity for background
  };

  useEffect(() => {
    // Fetch data for default location or user-selected location
    fetchWeatherData(location);
  }, [location]);

  return (
    <div className={`homepage ${humidity > 70 ? 'rainy-background' : 'sunny-background'}`}>
      <h1 className="header">Weather Prediction</h1>
      
      <div className="grid-container">
        {/* Left Side - Search and Current Weather */}
        <div className="left-section">
          <input
            type="text"
            placeholder="Search Location"
            className="search-input"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          {weatherData && (
            <div className="current-weather-card">
              <h2>{weatherData.temperature}¬∞C</h2>
              <p>{weatherData.location}</p>
              <p>{`"Expect. Take necessary precautions."`}</p>
            </div>
          )}
        </div>

        {/* Right Side - Warnings and 5-Day Forecast */}
        <div className="right-section">
          {weatherData && (
            <>
              <div className="warning-card">
                <p>‚ö†Ô∏è {weatherData.warning}</p>
                <p>‚ö†Ô∏è {weatherData.accidentWarning}</p>
              </div>

              <h2 className="forecast-title">5-Day Forecast</h2>
              <div className="forecast-container">
                {weatherData.forecast.map((day, index) => (
                  <div key={index} className="forecast-card">
                    <p>üåû Min: {day.minTemp}¬∞C Max: {day.maxTemp}¬∞C</p>
                    <p>üíß Humidity: {day.humidity}% üå¨Ô∏è Wind: {day.wind} km/h</p>
                    <p>üåßÔ∏è Rainfall: {day.rainfall} mm üöó Accidents: {day.accidents}</p>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
