import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    MenuItem,
    List,
    ListItem,
    ListItemText,
    Popper,
    Paper,
    ClickAwayListener,
} from '@mui/material';

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedLocation, setSelectedLocation] = useState(null);
    const [randomLocations, setRandomLocations] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                setRandomLocations(getRandomLocations(data, 3));
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const getRandomLocations = (locationsList, count) => {
        const shuffled = locationsList.sort(() => 0.5 - Math.random());
        return shuffled.slice(0, count);
    };

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchQuery(query);
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setFilteredLocations(matches);
        setAnchorEl(event.currentTarget);  // Show dropdown
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setLoading(true);
        setAnchorEl(null);  // Close dropdown on selection

        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                let adjustedAccidents = data.map(d => {
                    let accidents = d.total_accidents;
                    if (d.rainfall > 70) accidents += 2;
                    else if (d.rainfall > 65) accidents += 1;
                    return { ...d, total_accidents: accidents };
                });

                setPredictions(adjustedAccidents);
                setRainfallWarning(adjustedAccidents[0].rainfall > 50 ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(adjustedAccidents.some(d => d.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    return (
        <Container maxWidth="md" style={{ padding: "20px", backgroundColor: "transparent" }}>
            <Typography variant="h4" align="center" gutterBottom style={{ color: "#00796b", fontWeight: "bold" }}>
                Weather Prediction
            </Typography>

            <TextField
                fullWidth
                label="Search Location"
                variant="outlined"
                value={searchQuery}
                onChange={handleSearchChange}
                style={{ marginBottom: "20px" }}
                onClick={(e) => setAnchorEl(e.currentTarget)}
            />

            <Popper open={Boolean(anchorEl)} anchorEl={anchorEl} placement="bottom-start">
                <ClickAwayListener onClickAway={handleDropdownClose}>
                    <Paper elevation={3}>
                        <List>
                            {filteredLocations.map((location, index) => (
                                <ListItem
                                    button
                                    key={index}
                                    onClick={() => handleLocationClick(location)}
                                >
                                    <ListItemText primary={location} />
                                </ListItem>
                            ))}
                        </List>
                    </Paper>
                </ClickAwayListener>
            </Popper>

            <Box display="flex" justifyContent="space-around" flexWrap="wrap" mt={2}>
                {randomLocations.map((location, index) => (
                    <Card
                        key={index}
                        onClick={() => handleLocationClick(location)}
                        variant="outlined"
                        style={{
                            width: "30%",
                            margin: "10px",
                            cursor: "pointer",
                            backgroundColor: selectedLocation === location ? "#e0f7fa" : "white",
                        }}
                    >
                        <CardContent>
                            <Typography variant="h6" align="center">{location}</Typography>
                            <Typography variant="body2" align="center">Min Temp: {Math.floor(Math.random() * 10 + 15)}째C</Typography>
                            <Typography variant="body2" align="center">Max Temp: {Math.floor(Math.random() * 15 + 25)}째C</Typography>
                            <Typography variant="body2" align="center">Wind Speed: {Math.floor(Math.random() * 15 + 5)} km/h</Typography>
                            <Typography variant="body2" align="center">Humidity: {Math.floor(Math.random() * 40 + 30)}%</Typography>
                        </CardContent>
                    </Card>
                ))}
            </Box>

            {loading && <CircularProgress />}

            {predictions.length > 0 && selectedLocation && (
                <Box mt={4}>
                    <Typography variant="h5" align="center">Predictions for {selectedLocation}</Typography>
                    {predictions.map((prediction, index) => {
                        const date = new Date();
                        date.setDate(date.getDate() + index);
                        const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });

                        return (
                            <Box key={index} mt={2}>
                                <Typography variant="h6">{dayName} - {date.toLocaleDateString()}</Typography>
                                <Typography variant="body1">Min Temperature: {prediction.min_temp} 째C</Typography>
                                <Typography variant="body1">Max Temperature: {prediction.max_temp} 째C</Typography>
                                <Typography variant="body1">Humidity 9AM: {prediction.humidity_9am} %</Typography>
                                <Typography variant="body1">Humidity 3PM: {prediction.humidity_3pm} %</Typography>
                                <Typography variant="body1">Wind Speed 9AM: {prediction.wind_speed_9am} km/h</Typography>
                                <Typography variant="body1">Wind Speed 3PM: {prediction.wind_speed_3pm} km/h</Typography>
                                <Typography variant="body1">Predicted Rainfall: {prediction.rainfall} mm</Typography>
                                <Typography variant="body1">Total Accidents Predicted: {prediction.total_accidents}</Typography>
                            </Box>
                        );
                    })}
                </Box>
            )}

            {accidentWarning && <Alert severity="warning" style={{ marginTop: '20px' }}>{accidentWarning}</Alert>}
            {rainfallWarning && <Alert severity="warning" style={{ marginTop: '20px' }}>{rainfallWarning}</Alert>}
        </Container>
    );
}

export default Homepage;
