// WeatherInfoCard.jsx
import React from "react";
import { Card, CardContent, Typography, Grid } from "@mui/material";

const WeatherInfoCard = ({ title, value, icon, cardStyle }) => {
  return (
    <Card style={{ ...cardStyle, display: "flex", justifyContent: "center", alignItems: "center" }}>
      <CardContent style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        {/* Set a fixed size for the icon */}
        <div style={{ fontSize: "60px", marginBottom: "10px" }}>
          {icon}
        </div>
        <Grid container spacing={1} alignItems="center" justifyContent="center">
          <Grid item>
            <Typography 
              variant="h6" 
              align="center" 
              style={{ color: "white", fontSize: "24px" }} // Title font size
            >
              {title}
            </Typography>
          </Grid>
          <Grid item>
            <Typography 
              variant="h4" 
              align="center" 
              style={{ color: "white", fontSize: "32px" }} // Value font size
            >
              {value}
            </Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default WeatherInfoCard;
