import React, { useEffect, useState } from 'react';
import {
    Container,
    Typography,
    Grid,
    Card,
    TextField,
    Button,
} from '@mui/material';
import { motion } from 'framer-motion';
import { BsDroplet, BsSun, BsThermometerHalf } from 'react-icons/bs';
import { FaCloudRain } from 'react-icons/fa';
import './HomePage.css'; // Make sure to import your CSS

const Homepage = () => {
    const [location, setLocation] = useState('');
    const [predictions, setPredictions] = useState([]);
    
    const fetchData = async () => {
        // Fetch predictions based on the selected location
        const response = await fetch(`/api/weather?location=${location}`);
        const data = await response.json();
        setPredictions(data);
    };

    useEffect(() => {
        // Default fetch for random location on initial load
        fetchData();
    }, []);

    const handleSearch = (e) => {
        if (e.key === 'Enter') {
            fetchData();
        }
    };

    return (
        <motion.div
            className="homepage-container"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
        >
            <Container>
                {/* Search Bar */}
                <TextField
                    variant="outlined"
                    label="Enter Location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    onKeyPress={handleSearch}
                    fullWidth
                    style={{ marginBottom: '20px' }}
                />
                
                {/* Average Temperature Section */}
                <Typography variant="h4" align="center" style={{ color: 'white', marginBottom: '20px' }}>
                    Average Temp: {predictions[0]?.average_temp || 0}°C
                </Typography>

                {/* Info Cards */}
                <Grid container spacing={2} justifyContent="center">
                    <Grid item xs={12} sm={6} md={3}>
                        <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", borderRadius: "10px", boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)", padding: "10px", textAlign: 'center' }}>
                            <BsDroplet size={50} color="#00f" />
                            <Typography variant="h6" style={{ color: 'white' }}>Humidity</Typography>
                            <Typography variant="body1" style={{ color: 'white' }}>{predictions[0]?.humidity || 0}%</Typography>
                            <Typography variant="caption" style={{ color: 'white' }}>Humidity is making it feel warmer.</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", borderRadius: "10px", boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)", padding: "10px", textAlign: 'center' }}>
                            <BsThermometerHalf size={50} color="#f00" />
                            <Typography variant="h6" style={{ color: 'white' }}>Min Temp</Typography>
                            <Typography variant="body1" style={{ color: 'white' }}>{predictions[0]?.min_temp || 0}°C</Typography>
                            <Typography variant="caption" style={{ color: 'white' }}>Stay warm!</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", borderRadius: "10px", boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)", padding: "10px", textAlign: 'center' }}>
                            <BsThermometerHalf size={50} color="#0f0" />
                            <Typography variant="h6" style={{ color: 'white' }}>Max Temp</Typography>
                            <Typography variant="body1" style={{ color: 'white' }}>{predictions[0]?.max_temp || 0}°C</Typography>
                            <Typography variant="caption" style={{ color: 'white' }}>Enjoy the warmth!</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", borderRadius: "10px", boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)", padding: "10px", textAlign: 'center' }}>
                            <FaCloudRain size={50} color="#00f" />
                            <Typography variant="h6" style={{ color: 'white' }}>Rainfall</Typography>
                            <Typography variant="body1" style={{ color: 'white' }}>{predictions[0]?.rainfall || 0}mm</Typography>
                            <Typography variant="caption" style={{ color: 'white' }}>Grab your umbrella!</Typography>
                        </Card>
                    </Grid>
                </Grid>
            </Container>
        </motion.div>
    );
}

export default Homepage;
