import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    Button,
    FormControl,
    Select,
    MenuItem,
    InputLabel,
    Alert,
} from '@mui/material';

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [selectedLocation, setSelectedLocation] = useState('');
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [daysAhead, setDaysAhead] = useState(1); // State for the number of days ahead

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => setLocations(data))
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const handlePredict = () => {
        setLoading(true);
        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location: selectedLocation, days_ahead: daysAhead }), // Include daysAhead in the request
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                const adjustedPredictions = data.map((dayPrediction) => {
                    let adjustedAccidents = dayPrediction.total_accidents;
                    if (dayPrediction.rainfall > 70) {
                        adjustedAccidents += 2;  // Add 2 if rainfall > 70
                    } else if (dayPrediction.rainfall > 65) {
                        adjustedAccidents += 1;  // Add 1 if rainfall > 50
                    }

                    return { ...dayPrediction, total_accidents: adjustedAccidents };
                });

                setPredictions(adjustedPredictions);
                setRainfallWarning(adjustedPredictions.some(pred => pred.rainfall > 50) ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(adjustedPredictions.some(pred => pred.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    return (
        <Container
            maxWidth="md"
            style={{
                padding: "20px",
                backgroundColor: "transparent",
            }}
        >
            <Typography
                variant="h4"
                align="center"
                gutterBottom
                style={{ color: "#00796b", fontWeight: "bold" }}
            >
                Weather Prediction
            </Typography>

            <Card variant="outlined">
                <CardContent>
                    <FormControl fullWidth variant="outlined" style={{ marginBottom: "20px" }}>
                        <InputLabel>Select Location</InputLabel>
                        <Select
                            value={selectedLocation}
                            onChange={e => setSelectedLocation(e.target.value)}
                            label="Select Location"
                        >
                            <MenuItem value="">
                                <em>Select Location</em>
                            </MenuItem>
                            {locations.map((location, index) => (
                                <MenuItem key={index} value={location}>{location}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <FormControl fullWidth variant="outlined" style={{ marginBottom: "20px" }}>
                        <InputLabel>Select Days Ahead</InputLabel>
                        <Select
                            value={daysAhead}
                            onChange={e => setDaysAhead(e.target.value)}
                            label="Select Days Ahead"
                        >
                            {[1, 2, 3, 4, 5].map(day => (
                                <MenuItem key={day} value={day}>{day}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handlePredict}
                        disabled={!selectedLocation || loading}
                    >
                        {loading ? <CircularProgress size={24} /> : 'Get Prediction'}
                    </Button>

                    {predictions.length > 0 && (
                        <Box mt={4}>
                            <Typography variant="h5" align="center">
                                Predictions for {selectedLocation}
                            </Typography>
                            {predictions.map((pred, index) => (
                                <Box key={index} mt={2}>
                                    <Typography variant="h6">Day {index + 1}</Typography>
                                    <Typography variant="body1">Min Temperature: {pred.min_temp} °C</Typography>
                                    <Typography variant="body1">Max Temperature: {pred.max_temp} °C</Typography>
                                    <Typography variant="body1">Humidity 9AM: {pred.humidity_9am} %</Typography>
                                    <Typography variant="body1">Humidity 3PM: {pred.humidity_3pm} %</Typography>
                                    <Typography variant="body1">Wind Speed 9AM: {pred.wind_speed_9am} km/h</Typography>
                                    <Typography variant="body1">Wind Speed 3PM: {pred.wind_speed_3pm} km/h</Typography>
                                    <Typography variant="body1">Predicted Rainfall: {pred.rainfall} mm</Typography>
                                    <Typography variant="body1">Total Accidents Predicted: {pred.total_accidents}</Typography>
                                </Box>
                            ))}
                        </Box>
                    )}

                    {accidentWarning && (
                        <Alert severity="warning" style={{ marginTop: '20px' }}>
                            {accidentWarning}
                        </Alert>
                    )}

                    {rainfallWarning && (
                        <Alert severity="warning" style={{ marginTop: '20px' }}>
                            {rainfallWarning}
                        </Alert>
                    )}
                </CardContent>
            </Card>
        </Container>
    );
}

export default Homepage;
