import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    Popper,
    Paper,
    Grid,
    ClickAwayListener,
} from '@mui/material';
import { motion } from 'framer-motion';
import SearchIcon from '@mui/icons-material/Search'; // Import Material UI Search Icon
import CloudIcon from '@mui/icons-material/Cloud'; // Import Cloud Icon
import SunnyIcon from '@mui/icons-material/WbSunny'; // Import Sunny Icon
import RainyIcon from '@mui/icons-material/Water'; // Import Rain Icon
import './HomePage.css'; // Import your CSS for animations
import TempIcon from '@mui/icons-material/AcUnit'; // Example icon
import HumidityIcon from '@mui/icons-material/Opacity'; // Example icon
import WindSpeedIcon from '@mui/icons-material/Air'; // Example icon
import ThermostatAutoIcon from '@mui/icons-material/ThermostatAuto';
function Homepage() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [selectedLocation, setSelectedLocation] = useState('');
    const [anchorEl, setAnchorEl] = useState(null);
    const [searchInput, setSearchInput] = useState('');
    const [backgroundStyles, setBackgroundStyles] = useState({});

    // Fetch locations
    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                setFilteredLocations(data); // Initialize with all locations
                // Randomly select a location for initial display
                const randomLocation = data[Math.floor(Math.random() * data.length)];
                setSelectedLocation(randomLocation);
                fetchPredictions(randomLocation); // Fetch predictions for the random location
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const fetchPredictions = (location) => {
        setLoading(true);
        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => response.json())
            .then(data => {
                setPredictions(data);
                const humidity = data[0]?.humidity_9am || 0;
                setBackgroundStyles(getBackgroundStyles(humidity)); // Update background styles
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchInput(query);
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setFilteredLocations(matches);
        setAnchorEl(event.currentTarget); // Show dropdown
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setSearchInput(location); // Set the search bar to the selected location
        fetchPredictions(location); // Fetch predictions for the selected location
        setAnchorEl(null);
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    const getBackgroundStyles = (humidity) => {
        if (humidity > 70) {
            return {
                className: 'rainy-background',
            };
        } else if (humidity > 30) {
            return {
                className: 'cloudy-background',
            };
        }
        return {
            className: 'sunny-background',
        };
    };

    // Calculate average temperature
    const calculateAverageTemp = () => {
        if (predictions.length > 0) {
            const maxTemp = predictions[0].max_temp;
            const minTemp = predictions[0].min_temp;
            return ((maxTemp + minTemp) / 2).toFixed(1);
        }
        return 0;
    };

    // Get weather condition based on predictions
    const getWeatherCondition = () => {
        if (predictions.length > 0) {
            if (predictions[0].rainfall > 0) {
                return 'Rainy Day';
            }
            if (predictions[0].max_temp > 30) {
                return 'Sunny Day';
            }
            return 'Cloudy Day';
        }
        return '';
    };

    const weatherCondition = getWeatherCondition();
    const averageTemp = calculateAverageTemp();

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className={backgroundStyles.className}
            style={{ height: "100vh", color: "white", padding: "20px", width: '100%', overflow: 'hidden' }}
        >
            <Container maxWidth="xl" style={{ paddingLeft: 0, paddingRight: 0 }}>
                <Grid container spacing={3} style={{ height: '100%' }}>
                    {/* Left Section */}
                    <Grid item xs={12} md={4}>
                        <Typography variant="h4" gutterBottom style={{ fontWeight: "bold", color: "#BBDEFB" }}>
                            Weather Prediction
                        </Typography>

                        <TextField
                            fullWidth
                            label="Search Location"
                            variant="outlined"
                            onChange={handleSearchChange}
                            value={searchInput}
                            style={{
                                marginBottom: "20px",
                                backgroundColor: "#1C1C1C",
                                color: "white",
                                borderRadius: "10px",
                                boxShadow: "0 2px 5px rgba(0, 0, 0, 0.5)"
                            }}
                            InputLabelProps={{ style: { color: '#BBDEFB' } }}
                            InputProps={{
                                style: { color: 'white' },
                                sx: { borderRadius: '10px' },
                                startAdornment: <SearchIcon style={{ color: 'white' }} /> // Add search icon
                            }}
                        />

                        {/* Show filtered locations */}
                        <Popper open={Boolean(anchorEl)} anchorEl={anchorEl} placement="bottom-start">
                            <Paper>
                                <ClickAwayListener onClickAway={handleDropdownClose}>
                                    <Box>
                                        {filteredLocations.map((location, index) => (
                                            <Typography
                                                key={index}
                                                onClick={() => handleLocationClick(location)}
                                                style={{
                                                    padding: "10px",
                                                    cursor: "pointer",
                                                    backgroundColor: '#f0f0f0',
                                                    color: '#000',
                                                    borderRadius: '5px',
                                                    margin: '5px 0'
                                                }}
                                            >
                                                {location}
                                            </Typography>
                                        ))}
                                    </Box>
                                </ClickAwayListener>
                            </Paper>
                        </Popper>

                        {loading && <CircularProgress color="inherit" />}

                        {/* Combined Weather Info Box */}
                        {predictions.length > 0 && (
                            <Card style={{
                                marginTop: "20px",
                                padding: "20px",
                                backgroundColor: "rgba(255, 255, 255, 0.2)",
                                borderRadius: "10px",
                                boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)"
                            }}>
                                <CardContent>
                                    <Typography variant="h5" align="center" style={{ color: '#BBDEFB' }}>Weather Overview</Typography>
                                    <Typography variant="h6" align="center" style={{ color: '#FFEB3B' }}>{weatherCondition}</Typography>
                                    <Typography variant="body1" align="center" style={{ color: 'white' }}>
                                        {weatherCondition === 'Rainy Day' ? <RainyIcon style={{ color: 'yellow' }} /> : weatherCondition === 'Sunny Day' ? <SunnyIcon style={{ color: 'yellow' }} /> : <CloudIcon style={{ color: 'white' }} />}
                                        {weatherCondition === 'Rainy Day' ? ' Today the expectation is rainy, with a high temp of ' + predictions[0].max_temp + '°C. Grab your umbrella when going outside.' :
                                         weatherCondition === 'Sunny Day' ? ' Expect a sunny day with a high temp of ' + predictions[0].max_temp + '°C. Enjoy the sunshine!' :
                                         ' It might be cloudy today with a high temp of ' + predictions[0].max_temp + '°C. Stay safe!'}
                                    </Typography>
                                    <Typography variant="body1" align="center" style={{ color: 'white' }}>
                                        <strong>Average Temp: {averageTemp}°C </strong>
                                    </Typography>
                                </CardContent>
                            </Card>
                        )}

                        {/* Four Individual Info Boxes */}
                        <Grid container spacing={2} style={{ marginTop: "20px" }}>
                            <Grid item xs={6}>
                                <Card style={{
                                    backgroundColor: "rgba(255, 255, 255, 0.2)",
                                    borderRadius: "10px",
                                    boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
                                    textAlign: 'center'
                                }}>
                                    <CardContent>
                                    <ThermostatAutoIcon></ThermostatAutoIcon><Typography variant="h6" style={{ color: '#BBDEFB' }}>Min Temp</Typography>
                                        <Typography variant="h5" style={{ color: 'white' }}>{predictions[0]?.min_temp || 'N/A'}°C</Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                            <Grid item xs={6}>
                                <Card style={{
                                    backgroundColor: "rgba(255, 255, 255, 0.2)",
                                    borderRadius: "10px",
                                    boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
                                    textAlign: 'center'
                                }}>
                                    <CardContent>
                                        <Typography variant="h6" style={{ color: '#BBDEFB' }}>Max Temp</Typography>
                                        <Typography variant="h5" style={{ color: 'white' }}>{predictions[0]?.max_temp || 'N/A'}°C</Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                            <Grid item xs={6}>
                                <Card style={{
                                    backgroundColor: "rgba(255, 255, 255, 0.2)",
                                    borderRadius: "10px",
                                    boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
                                    textAlign: 'center'
                                }}>
                                    <CardContent>
                                        <Typography variant="h6" style={{ color: '#BBDEFB' }}>Humidity</Typography>
                                        <Typography variant="h5" style={{ color: 'white' }}>{predictions[0]?.humidity_9am || 'N/A'}%</Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                            <Grid item xs={6}>
                                <Card style={{
                                    backgroundColor: "rgba(255, 255, 255, 0.2)",
                                    borderRadius: "10px",
                                    boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
                                    textAlign: 'center'
                                }}>
                                    <CardContent>
                                        <Typography variant="h6" style={{ color: '#BBDEFB' }}>Wind Speed</Typography>
                                        <Typography variant="h5" style={{ color: 'white' }}>{predictions[0]?.wind_speed_9am || 'N/A'} km/h</Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                        </Grid>
                    </Grid>
                    {/* Right Section */}
                    <Grid item xs={12} md={8}>
                        <Typography variant="h4" gutterBottom style={{ fontWeight: "bold", color: "#BBDEFB" }}>
                            Accidents Prediction
                        </Typography>
                        {/* Display accident data here (similar to weather prediction) */}
                        {/* For simplicity, assuming accident predictions are fetched similarly */}
                        {/* Replace below with actual data fetching and display */}
                        <Card style={{
                            backgroundColor: "rgba(255, 255, 255, 0.2)",
                            borderRadius: "10px",
                            boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
                            padding: "20px"
                        }}>
                            <CardContent>
                                <Typography variant="h6" style={{ color: '#BBDEFB' }}>Predicted Accidents</Typography>
                                <Typography variant="h5" style={{ color: 'white' }}>
                                    Based on today's rainfall of {predictions[0]?.rainfall || 'N/A'} mm, the predicted accidents are:
                                    {/* Example accident prediction logic (to be replaced with actual API call) */}
                                    {predictions[0]?.rainfall > 10 ? ' High risk of accidents' : ' Low risk of accidents'}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                </Grid>
            </Container>
        </motion.div>
    );
}

export default Homepage;
