import React, { useState, useEffect } from 'react';

const Homepage = () => {
    const [locations, setLocations] = useState([]);
    const [prediction, setPrediction] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => setLocations(data))
            .catch(error => console.error('Error fetching locations:', error));
    }, []);

    const handlePredict = async (location) => {
        const response = await fetch('http://127.0.0.1:8000/predict', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        });
        const data = await response.json();
        setPrediction(data);
    };

    return (
        <div>
            <h1>Weather Prediction</h1>
            <select onChange={(e) => handlePredict(e.target.value)}>
                <option value="">Select a location</option>
                {locations.map((loc, index) => (
                    <option key={index} value={loc}>{loc}</option>
                ))}
            </select>

            {prediction && (
                <div>
                    <h2>Prediction Results</h2>
                    <p>Min Temperature: {prediction.min_temp}</p>
                    <p>Max Temperature: {prediction.max_temp}</p>
                    <p>Humidity 9am: {prediction.humidity_9am}</p>
                    <p>Humidity 3pm: {prediction.humidity_3pm}</p>
                    <p>Wind Speed 9am: {prediction.wind_speed_9am}</p>
                    <p>Wind Speed 3pm: {prediction.wind_speed_3pm}</p>
                    <p>Total Accidents Predicted: {prediction.total_accidents}</p>
                    <p>Predicted Rainfall: {prediction.rainfall} mm</p>
                    {prediction.total_accidents > 5 && (
                        <p style={{ color: 'red' }}>Warning: High risk of accidents!</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Homepage;
