import React, { useState } from 'react';

const HomePage = () => {
    const [weatherData, setWeatherData] = useState({
        location: '',
        minTemp: 0,
        maxTemp: 0,
        humidity9am: 0,
        humidity3pm: 0,
        windSpeed9am: 0,
        windSpeed3pm: 0,
    });

    const [accidentData, setAccidentData] = useState({ rainfall: 0 });

    const [weatherPrediction, setWeatherPrediction] = useState(null);
    const [accidentPrediction, setAccidentPrediction] = useState(null);

    const handleWeatherChange = (e) => {
        setWeatherData({
            ...weatherData,
            [e.target.name]: parseFloat(e.target.value),
        });
    };

    const handleAccidentChange = (e) => {
        setAccidentData({
            ...accidentData,
            [e.target.name]: parseFloat(e.target.value),
        });
    };

    const predictWeather = async (e) => {
        e.preventDefault();
        const response = await fetch('/predict/weather/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(weatherData),
        });
        const data = await response.json();
        setWeatherPrediction(data.predictions);
    };

    const predictAccidents = async (e) => {
        e.preventDefault();
        const response = await fetch('/predict/accidents/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(accidentData),
        });
        const data = await response.json();
        setAccidentPrediction(data.predicted_accidents);
    };

    return (
        <div>
            <h1>Weather and Accident Prediction</h1>

            <h2>Weather Prediction</h2>
            <form onSubmit={predictWeather}>
                <input type="text" name="location" placeholder="Location" onChange={handleWeatherChange} required />
                <input type="number" name="minTemp" placeholder="Min Temperature" onChange={handleWeatherChange} required />
                <input type="number" name="maxTemp" placeholder="Max Temperature" onChange={handleWeatherChange} required />
                <input type="number" name="humidity9am" placeholder="Humidity at 9 AM" onChange={handleWeatherChange} required />
                <input type="number" name="humidity3pm" placeholder="Humidity at 3 PM" onChange={handleWeatherChange} required />
                <input type="number" name="windSpeed9am" placeholder="Wind Speed at 9 AM" onChange={handleWeatherChange} required />
                <input type="number" name="windSpeed3pm" placeholder="Wind Speed at 3 PM" onChange={handleWeatherChange} required />
                <button type="submit">Get Weather Prediction</button>
            </form>
            {weatherPrediction && <div>Weather Predictions: {JSON.stringify(weatherPrediction)}</div>}

            <h2>Accident Prediction</h2>
            <form onSubmit={predictAccidents}>
                <input type="number" name="rainfall" placeholder="Rainfall" onChange={handleAccidentChange} required />
                <button type="submit">Get Accident Prediction</button>
            </form>
            {accidentPrediction && <div>Predicted Accidents: {accidentPrediction}</div>}
        </div>
    );
};

export default HomePage;
