import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [locations, setLocations] = useState([]);
    const [selectedLocation, setSelectedLocation] = useState('');
    const [predictions, setPredictions] = useState([]);

    useEffect(() => {
        const fetchLocations = async () => {
            const response = await axios.get('http://localhost:8000/locations');
            setLocations(response.data);
        };

        fetchLocations();
    }, []);

    const handleLocationChange = async (event) => {
        const location = event.target.value;
        setSelectedLocation(location);
        if (location) {
            const response = await axios.post('http://localhost:8000/predict', { location });
            setPredictions(response.data);
        } else {
            setPredictions([]); // Reset predictions if no location is selected
        }
    };

    return (
        <div>
            <h1>Weather Predictions</h1>
            <select value={selectedLocation} onChange={handleLocationChange}>
                <option value="">Select a location</option>
                {locations.map((location) => (
                    <option key={location} value={location}>{location}</option>
                ))}
            </select>
            {predictions.length > 0 && (
                <div>
                    <h2>Predictions for {selectedLocation}</h2>
                    <ul>
                        {predictions.map((prediction) => (
                            <li key={prediction.date}>
                                {prediction.day_of_week}, {prediction.date}: 
                                Min Temp: {prediction.min_temp}°C, 
                                Max Temp: {prediction.max_temp}°C, 
                                Humidity 9 AM: {prediction.humidity_9am}%, 
                                Humidity 3 PM: {prediction.humidity_3pm}%, 
                                Wind Speed 9 AM: {prediction.wind_speed_9am} km/h, 
                                Wind Speed 3 PM: {prediction.wind_speed_3pm} km/h, 
                                Total Accidents: {prediction.total_accidents}, 
                                Rainfall: {prediction.rainfall} mm
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
