import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    Popper,
    Paper,
    Grid,
    ClickAwayListener,
} from '@mui/material';
import { motion } from 'framer-motion';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import CloudIcon from '@mui/icons-material/Cloud';
import WaterIcon from '@mui/icons-material/Water'; // Use an icon for rainy weather
import './HomePage.css'; // Import your CSS for animations

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [selectedLocation, setSelectedLocation] = useState(null);
    const [anchorEl, setAnchorEl] = useState(null);
    const [searchInput, setSearchInput] = useState('');
    const [backgroundStyles, setBackgroundStyles] = useState({});

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                setFilteredLocations(data); // Initialize with all locations
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchInput(query);
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setFilteredLocations(matches);
        setAnchorEl(event.currentTarget); // Show dropdown
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setLoading(true);
        setAnchorEl(null);
        setSearchInput(''); // Clear the search input

        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => response.json())
            .then(data => {
                setPredictions(data);

                const humidity = data[0]?.humidity_9am || 0;
                setBackgroundStyles(getBackgroundStyles(humidity)); // Update background styles

                // Update warnings
                setRainfallWarning(data[0]?.rainfall > 50 ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(data.some(d => d.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    const getBackgroundStyles = (humidity) => {
        if (humidity > 70) {
            return {
                className: 'rainy-background', // Assign a class for rainy background
                icon: <WaterIcon fontSize="large" />
            };
        }
        return {
            className: 'sunny-background', // Assign a class for sunny background
            icon: <WbSunnyIcon fontSize="large" />
        };
    };

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={backgroundStyles.className} // Use the background class
            style={{
                height: "100vh",
                color: "white",
                padding: "20px"
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={3}>
                    {/* Left Section */}
                    <Grid item xs={4}>
                        <Typography variant="h4" gutterBottom style={{ fontWeight: "bold", color: "#BBDEFB" }}>
                            Weather Prediction
                        </Typography>

                        <TextField
                            fullWidth
                            label="Search Location"
                            variant="outlined"
                            onChange={handleSearchChange}
                            value={searchInput}
                            style={{ marginBottom: "20px", backgroundColor: "#1C2331", color: "white" }}
                            InputLabelProps={{ style: { color: '#BBDEFB' } }}
                        />

                        {/* Show filtered locations */}
                        <Popper open={Boolean(anchorEl)} anchorEl={anchorEl} placement="bottom-start">
                            <Paper>
                                <ClickAwayListener onClickAway={handleDropdownClose}>
                                    <Box>
                                        {filteredLocations.map((location, index) => (
                                            <Typography
                                                key={index}
                                                onClick={() => handleLocationClick(location)}
                                                style={{ padding: "10px", cursor: "pointer", backgroundColor: '#f0f0f0', color: '#000' }}
                                            >
                                                {location}
                                            </Typography>
                                        ))}
                                    </Box>
                                </ClickAwayListener>
                            </Paper>
                        </Popper>

                        {loading && <CircularProgress color="inherit" />}

                        <Box mt={2}>
                            {rainfallWarning && <Alert severity="warning">{rainfallWarning}</Alert>}
                            {accidentWarning && <Alert severity="warning">{accidentWarning}</Alert>}
                        </Box>
                    </Grid>

                    {/* Right Section */}
                    <Grid item xs={8}>
                        {selectedLocation && predictions.length > 0 && (
                            <motion.div initial={{ y: -10 }} animate={{ y: 0 }} transition={{ duration: 0.5 }}>
                                <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", padding: "20px", color: "#ECEFF1" }}>
                                    <CardContent>
                                        <Typography variant="h3" align="center">{predictions[0].max_temp}°C</Typography>
                                        <Typography variant="h6" align="center">{selectedLocation}</Typography>
                                        {backgroundStyles.icon}
                                        <Typography variant="body1" align="center">Wind Speed: {predictions[0].wind_speed_9am} km/h</Typography>
                                        <Typography variant="body1" align="center">Humidity: {predictions[0].humidity_9am}%</Typography>
                                        <Typography variant="body1" align="center">Rainfall: {predictions[0].rainfall} mm</Typography>
                                        <Typography variant="body1" align="center">Accidents Predicted: {predictions[0].total_accidents}</Typography>
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}
                    </Grid>
                </Grid>
            </Container>
        </motion.div>
    );
}

export default Homepage;
