// components/WeatherInfoCard.jsx
import React from "react";
import { Card, CardContent, Typography } from "@mui/material";

function WeatherInfoCard({ icon: Icon, label, value, unit }) {
  return (
    <Card
      style={{
        backgroundColor: "rgba(255, 255, 255, 0.2)",
        borderRadius: "10px",
        boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
        height: "100px",
        textAlign: "center",
      }}
    >
      <CardContent>
        <Icon style={{ color: "#BBDEFB", fontSize: 30 }} />
        <Typography variant="body2" style={{ color: "white" }}>{label}</Typography>
        <Typography variant="body2" style={{ color: "white" }}>{value}{unit}</Typography>
      </CardContent>
    </Card>
  );
}

export default WeatherInfoCard;
