import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Homepage = () => {
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState('');
  const [predictions, setPredictions] = useState(null);

  useEffect(() => {
    const fetchLocations = async () => {
      const response = await axios.get('data/locations.csv'); // Ensure this points to a CSV file with locations
      setLocations(response.data.map(row => row.Location)); // Adapt based on your data structure
    };

    fetchLocations();
  }, []);

  const handleLocationChange = async (event) => {
    const location = event.target.value;
    setSelectedLocation(location);

    if (location) {
      const response = await axios.post('http://localhost:8000/predict', { location });
      setPredictions(response.data);
    } else {
      setPredictions(null);
    }
  };

  return (
    <div>
      <h1>Weather and Accident Predictions</h1>
      <label htmlFor="location-select">Select a Location:</label>
      <select id="location-select" value={selectedLocation} onChange={handleLocationChange}>
        <option value="">--Select a Location--</option>
        {locations.map(location => (
          <option key={location} value={location}>{location}</option>
        ))}
      </select>

      {predictions && (
        <div>
          <h2>Predictions for {selectedLocation}</h2>
          <p>Min Temperature: {predictions.min_temp}°C</p>
          <p>Max Temperature: {predictions.max_temp}°C</p>
          <p>Humidity at 9 AM: {predictions.humidity_9am}%</p>
          <p>Humidity at 3 PM: {predictions.humidity_3pm}%</p>
          <p>Wind Speed at 9 AM: {predictions.wind_speed_9am} km/h</p>
          <p>Wind Speed at 3 PM: {predictions.wind_speed_3pm} km/h</p>
          <p>Total Accidents Prediction: {predictions.total_accidents}</p>
        </div>
      )}
    </div>
  );
};

export default Homepage;
