import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    List,
    ListItem,
    ListItemText,
    Popper,
    Paper,
    Grid,
    ClickAwayListener,
} from '@mui/material';
import { motion } from 'framer-motion';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import OpacityIcon from '@mui/icons-material/Opacity';
import AirIcon from '@mui/icons-material/Air';
import CloudIcon from '@mui/icons-material/Cloud';
import ThunderstormIcon from '@mui/icons-material/Thunderstorm';

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedLocation, setSelectedLocation] = useState(null);
    const [randomLocations, setRandomLocations] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                generateRandomLocations(data);
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const generateRandomLocations = (locationsList) => {
        const shuffled = locationsList.sort(() => 0.5 - Math.random());
        setRandomLocations(shuffled.slice(0, 3));
    };

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchQuery(query);
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setFilteredLocations(matches);
        setAnchorEl(event.currentTarget);
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setLoading(true);
        setAnchorEl(null);

        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => response.json())
            .then(data => {
                let adjustedAccidents = data.map(d => {
                    let accidents = d.total_accidents;
                    if (d.rainfall > 70) accidents += 2;
                    else if (d.rainfall > 65) accidents += 1;
                    return { ...d, total_accidents: accidents };
                });

                setPredictions(adjustedAccidents);
                setRainfallWarning(adjustedAccidents[0].rainfall > 50 ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(adjustedAccidents.some(d => d.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    const getBackgroundStyles = (humidity) => {
        if (humidity > 70) {
            return {
                background: 'linear-gradient(to right, #4b79a1, #283e51)',
                icon: <CloudIcon fontSize="large" />
            };
        }
        return {
            background: 'linear-gradient(to right, #56ccf2, #2f80ed)',
            icon: <WbSunnyIcon fontSize="large" />
        };
    };

    const backgroundStyles = getBackgroundStyles(predictions[0]?.humidity_9am || 50);

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            style={{
                height: "100vh",
                background: backgroundStyles.background,
                color: "white",
                padding: "20px"
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={3}>
                    {/* Left Section - 4 Columns */}
                    <Grid item xs={4}>
                        <Typography variant="h4" gutterBottom style={{ fontWeight: "bold", color: "#BBDEFB" }}>
                            Weather Prediction
                        </Typography>

                        <TextField
                            fullWidth
                            label="Search Location"
                            variant="outlined"
                            value={searchQuery}
                            onChange={handleSearchChange}
                            style={{ marginBottom: "20px", backgroundColor: "#1C2331", color: "white" }}
                            InputLabelProps={{ style: { color: '#BBDEFB' } }}
                        />

                        <Popper open={Boolean(anchorEl)} anchorEl={anchorEl} placement="bottom-start">
                            <ClickAwayListener onClickAway={handleDropdownClose}>
                                <Paper elevation={3}>
                                    <List>
                                        {filteredLocations.map((location, index) => (
                                            <ListItem button key={index} onClick={() => handleLocationClick(location)}>
                                                <ListItemText primary={location} />
                                            </ListItem>
                                        ))}
                                    </List>
                                </Paper>
                            </ClickAwayListener>
                        </Popper>

                        {loading && <CircularProgress color="inherit" />}

                        <Box mt={2}>
                            {rainfallWarning && <Alert severity="warning">{rainfallWarning}</Alert>}
                            {accidentWarning && <Alert severity="warning">{accidentWarning}</Alert>}
                        </Box>
                    </Grid>

                    {/* Right Section - 6 Columns */}
                    <Grid item xs={8}>
                        {selectedLocation && predictions.length > 0 && (
                            <motion.div initial={{ y: -10 }} animate={{ y: 0 }} transition={{ duration: 0.5 }}>
                                <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", padding: "20px", color: "#ECEFF1" }}>
                                    <CardContent>
                                        <Typography variant="h3" align="center">{predictions[0].max_temp}Â°C</Typography>
                                        <Typography variant="h6" align="center">{selectedLocation}</Typography>
                                        {backgroundStyles.icon}
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}

                        <Typography variant="h5" align="center" mt={4}>Random Locations</Typography>
                        <Grid container spacing={2} mt={1}>
                            {randomLocations.map((location, index) => (
                                <Grid item xs={4} key={index}>
                                    <Card style={{
                                        backgroundColor: "rgba(255, 255, 255, 0.2)",
                                        padding: "10px",
                                        textAlign: "center"
                                    }}>
                                        <Typography>{location}</Typography>
                                    </Card>
                                </Grid>
                            ))}
                        </Grid>
                    </Grid>
                </Grid>
            </Container>
        </motion.div>
    );
}

export default Homepage;
