import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchEngine from "./SearchEngine";
import Forecast from "./Forecast";
import {
  Container,
  Typography,
  CircularProgress,
  Box,
  Card,
  CardContent,
  TextField,
  Button,
} from "@mui/material";
import "../styles.css";

function HomePage() {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({
    loading: true,
    data: {},
    error: false,
  });

  const toDate = () => {
    const months = [
      "January", "February", "March", "April", "May", "June",
      "July", "August", "September", "October", "November", "December",
    ];
    const days = [
      "Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday",
    ];

    const currentDate = new Date();
    const date = `${days[currentDate.getDay()]} ${currentDate.getDate()} ${
      months[currentDate.getMonth()]
    }`;
    return date;
  };

  const search = async (event) => {
    event.preventDefault();
    setWeather({ ...weather, loading: true });

    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/predict?location=${query}`
      );
      setWeather({ data: response.data, loading: false, error: false });
    } catch (error) {
      setWeather({ ...weather, data: {}, error: true });
    }
  };

  return (
    <Container
      className="App"
      maxWidth="md"
      style={{
        padding: "20px",
        borderRadius: "10px",
        backgroundColor: "rgba(255, 255, 255, 0.9)",
      }}
    >
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        style={{ color: "#00796b", fontWeight: "bold" }}
      >
        Weather Forecast
      </Typography>

      <Box
        component="form"
        display="flex"
        justifyContent="center"
        mt={2}
        onSubmit={search}
      >
        <TextField
          variant="outlined"
          label="Enter Location"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{ backgroundColor: "#f0f0f0", borderRadius: "5px" }}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          style={{ marginLeft: "10px" }}
        >
          Search
        </Button>
      </Box>

      <Box display="flex" justifyContent="center" flexWrap="wrap" mt={4}>
        <Card className="card">
          <CardContent>
            {weather.loading ? (
              <Box display="flex" justifyContent="center">
                <CircularProgress color="primary" />
              </Box>
            ) : weather.error ? (
              <Typography variant="h6" color="error" align="center">
                Sorry, location not found. Please try again.
              </Typography>
            ) : (
              <Forecast weather={weather} toDate={toDate} />
            )}
          </CardContent>
        </Card>
        <Card className="card">
          <CardContent>
            <Typography variant="h5" align="center">
              Card 2
            </Typography>
            <Typography variant="body2" align="center">
              Additional weather information can go here.
            </Typography>
          </CardContent>
        </Card>
        <Card className="card">
          <CardContent>
            <Typography variant="h5" align="center">
              Card 3
            </Typography>
            <Typography variant="body2" align="center">
              Further statistics or graphics can be included here.
            </Typography>
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
}

export default HomePage;
