import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    MenuItem,
    List,
    ListItem,
    ListItemText,
    Popper,
    Paper,
    ClickAwayListener,
} from '@mui/material';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import OpacityIcon from '@mui/icons-material/Opacity';
import AirIcon from '@mui/icons-material/Air';
import CloudIcon from '@mui/icons-material/Cloud';
import ThunderstormIcon from '@mui/icons-material/Thunderstorm';

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedLocation, setSelectedLocation] = useState(null);
    const [randomLocations, setRandomLocations] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                setRandomLocations(getRandomLocations(data, 3));
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    const getRandomLocations = (locationsList, count) => {
        const shuffled = locationsList.sort(() => 0.5 - Math.random());
        return shuffled.slice(0, count);
    };

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchQuery(query);
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setFilteredLocations(matches);
        setAnchorEl(event.currentTarget);
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setLoading(true);
        setAnchorEl(null);

        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => response.json())
            .then(data => {
                let adjustedAccidents = data.map(d => {
                    let accidents = d.total_accidents;
                    if (d.rainfall > 70) accidents += 2;
                    else if (d.rainfall > 65) accidents += 1;
                    return { ...d, total_accidents: accidents };
                });

                setPredictions(adjustedAccidents);
                setRainfallWarning(adjustedAccidents[0].rainfall > 50 ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(adjustedAccidents.some(d => d.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    const getGradientBackground = (rainfall) => {
        if (rainfall > 50) return 'linear-gradient(to right, #4b79a1, #283e51)';
        if (rainfall > 20) return 'linear-gradient(to right, #gray, #a8c0ff)';
        return 'linear-gradient(to right, #56ccf2, #2f80ed)';
    };

    return (
        <Container maxWidth="md" style={{ padding: "20px", backgroundColor: "transparent" }}>
            <Typography variant="h4" align="center" gutterBottom style={{ color: "#00796b", fontWeight: "bold" }}>
                Weather Prediction
            </Typography>

            <TextField
                fullWidth
                label="Search Location"
                variant="outlined"
                value={searchQuery}
                onChange={handleSearchChange}
                style={{ marginBottom: "20px" }}
                onClick={(e) => setAnchorEl(e.currentTarget)}
            />

            <Popper open={Boolean(anchorEl)} anchorEl={anchorEl} placement="bottom-start">
                <ClickAwayListener onClickAway={handleDropdownClose}>
                    <Paper elevation={3}>
                        <List>
                            {filteredLocations.map((location, index) => (
                                <ListItem
                                    button
                                    key={index}
                                    onClick={() => handleLocationClick(location)}
                                >
                                    <ListItemText primary={location} />
                                </ListItem>
                            ))}
                        </List>
                    </Paper>
                </ClickAwayListener>
            </Popper>

            {loading && <CircularProgress />}

            {predictions.length > 0 && selectedLocation && (
                <Box mt={4}>
                    <Typography variant="h5" align="center">Predictions for {selectedLocation}</Typography>
                    <Box display="flex" flexDirection="column" gap={2}>
                        {predictions.map((prediction, index) => {
                            const date = new Date();
                            date.setDate(date.getDate() + index);
                            const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });

                            return (
                                <Card
                                    key={index}
                                    variant="outlined"
                                    style={{
                                        background: getGradientBackground(prediction.rainfall),
                                        boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
                                        padding: "16px",
                                        borderRadius: "8px",
                                    }}
                                >
                                    <CardContent>
                                        <Typography variant="h6" gutterBottom>{dayName} - {date.toLocaleDateString()}</Typography>
                                        <Box display="flex" alignItems="center" gap={2}>
                                            <WbSunnyIcon /> <Typography>Min Temp: {prediction.min_temp} °C</Typography>
                                            <WbSunnyIcon /> <Typography>Max Temp: {prediction.max_temp} °C</Typography>
                                        </Box>
                                        <Box display="flex" alignItems="center" gap={2}>
                                            <OpacityIcon /> <Typography>Humidity 9AM: {prediction.humidity_9am} %</Typography>
                                            <OpacityIcon /> <Typography>Humidity 3PM: {prediction.humidity_3pm} %</Typography>
                                        </Box>
                                        <Box display="flex" alignItems="center" gap={2}>
                                            <AirIcon /> <Typography>Wind 9AM: {prediction.wind_speed_9am} km/h</Typography>
                                            <AirIcon /> <Typography>Wind 3PM: {prediction.wind_speed_3pm} km/h</Typography>
                                        </Box>
                                        <Box display="flex" alignItems="center" gap={2}>
                                            <CloudIcon /> <Typography>Rainfall: {prediction.rainfall} mm</Typography>
                                            <ThunderstormIcon /> <Typography>Accidents: {prediction.total_accidents}</Typography>
                                        </Box>
                                    </CardContent>
                                </Card>
                            );
                        })}
                    </Box>
                </Box>
            )}

            {accidentWarning && <Alert severity="warning" style={{ marginTop: '20px' }}>{accidentWarning}</Alert>}
            {rainfallWarning && <Alert severity="warning" style={{ marginTop: '20px' }}>{rainfallWarning}</Alert>}
        </Container>
    );
}

export default Homepage;
