// HomePage.jsx

import React, { useState, useEffect } from 'react';
import { Card, Typography, Box, Grid, TextField, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';

const HomePage = () => {
    const [location, setLocation] = useState('Hanoi, Vietnam');
    const [weatherData, setWeatherData] = useState(null);
    const [hourlyForecast, setHourlyForecast] = useState([]);
    const [dailyForecast, setDailyForecast] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchWeatherData(location);
    }, []);

    const fetchWeatherData = async (loc) => {
        try {
            const response = await axios.post('http://localhost:8000/predict', { location: loc });
            setWeatherData(response.data[0]); // Assuming today's weather is first in the array
            setDailyForecast(response.data);  // Full 6-day forecast
            setHourlyForecast(generateHourlyForecast(response.data[0])); // Generate hourly data for today's prediction
            setError(null);
        } catch (err) {
            setError("Location not found or unable to fetch data.");
            console.error(err);
        }
    };

    const generateHourlyForecast = (dailyData) => {
        // Simulate hourly forecast based on daily data (for demo)
        return [
            { time: 'Now', temp: dailyData.max_temp },
            { time: '15:00', temp: dailyData.max_temp - 2 },
            { time: '16:00', temp: dailyData.max_temp - 4 },
            { time: '17:00', temp: dailyData.max_temp - 1 },
            { time: '18:00', temp: dailyData.max_temp + 3 },
            { time: '19:00', temp: dailyData.max_temp }
        ];
    };

    const handleSearch = () => {
        fetchWeatherData(location);
    };

    return (
        <Box sx={{ padding: 4, background: 'url(/rainy-background.jpg)', minHeight: '100vh', color: '#fff' }}>
            {/* Search Bar */}
            <Box display="flex" alignItems="center" mb={3}>
                <TextField 
                    variant="outlined"
                    placeholder="Search location..."
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    sx={{ backgroundColor: 'rgba(255, 255, 255, 0.2)', color: '#fff', borderRadius: '5px' }}
                    InputProps={{ style: { color: 'white' } }}
                />
                <IconButton onClick={handleSearch} sx={{ color: '#fff', ml: 1 }}>
                    <SearchIcon />
                </IconButton>
            </Box>

            {error && <Typography color="error">{error}</Typography>}

            {weatherData && (
                <Grid container spacing={4}>
                    {/* Main Weather Display */}
                    <Grid item xs={12} md={6}>
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography variant="h3">{weatherData.max_temp}°</Typography>
                            <Typography variant="h6">Rainy Day</Typography>
                            <Typography variant="body2">
                                Today, expect a rainy day with temperatures reaching a maximum of {weatherData.max_temp}°C. 
                                Make sure to grab your umbrella and raincoat before heading out.
                            </Typography>
                        </Card>
                    </Grid>

                    {/* Forecast Sections */}
                    <Grid item xs={12} md={6}>
                        {/* Hourly Forecast */}
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2, mb: 2 }}>
                            <Typography variant="h6">Hourly Forecast</Typography>
                            <Grid container spacing={1}>
                                {hourlyForecast.map((hour, idx) => (
                                    <Grid item xs={4} md={2} key={idx}>
                                        <Typography>{hour.time}</Typography>
                                        <Typography>{hour.temp}°</Typography>
                                    </Grid>
                                ))}
                            </Grid>
                        </Card>

                        {/* 6-Day Forecast */}
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography variant="h6">6-Day Forecast</Typography>
                            <Grid container spacing={1}>
                                {dailyForecast.map((day, idx) => (
                                    <Grid item xs={4} md={2} key={idx}>
                                        <Typography>{day.day_of_week}</Typography>
                                        <Typography>{day.max_temp}°</Typography>
                                    </Grid>
                                ))}
                            </Grid>
                        </Card>
                    </Grid>

                    {/* Additional Weather Details */}
                    <Grid item xs={6} md={3}>
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography>Feels Like</Typography>
                            <Typography>{weatherData.min_temp}°</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography>Humidity</Typography>
                            <Typography>{weatherData.humidity_9am}%</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography>Precipitation</Typography>
                            <Typography>{weatherData.rainfall} mm</Typography>
                        </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                        <Card sx={{ background: 'rgba(0, 0, 0, 0.5)', padding: 2 }}>
                            <Typography>Wind Speed</Typography>
                            <Typography>{weatherData.wind_speed_3pm} km/h</Typography>
                        </Card>
                    </Grid>
                </Grid>
            )}
        </Box>
    );
};

export default HomePage;
