// WeatherInfoCard.jsx
import React from "react";
import { Card, CardContent, Typography } from "@mui/material";

function WeatherInfoCard({ title, value, icon }) {
  return (
    <Card
      style={{
        backgroundColor: "rgba(255, 255, 255, 0.2)",
        borderRadius: "10px",
        boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
        height: "100px",
        textAlign: "center",
        padding: "40px",
        margin: "10px",
      }}
    >
      <CardContent>
        {icon}
        <Typography 
          variant="h6" 
          align="center" 
          style={{ color: "white", fontSize: "20px", }} // Increased font size
        >
          {title}
        </Typography>
        <Typography 
          variant="h1" 
          align="center" 
          style={{ color: "white", fontSize: "20px" }} // Increased font size
        >
          {value}
        </Typography>
      </CardContent>
    </Card>
  );
}

export default WeatherInfoCard;
