    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className={backgroundStyles.className} style={{ height: "100vh", color: "white", padding: "20px", width: "100%", overflow: "hidden" }}>
      <Container maxWidth="xl">
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <LocationSearch 
              searchInput={searchInput}
