import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    CircularProgress,
    Box,
    Card,
    CardContent,
    TextField,
    Alert,
    Popper,
    Paper,
    Grid,
    ClickAwayListener,
} from '@mui/material';
import { motion } from 'framer-motion';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import CloudIcon from '@mui/icons-material/Cloud';
import RainyIcon from '@mui/icons-material/Water'; // Use an icon for rainy weather
import './styles.css'; // Import your CSS for animations

function Homepage() {
    const [locations, setLocations] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [accidentWarning, setAccidentWarning] = useState('');
    const [rainfallWarning, setRainfallWarning] = useState('');
    const [selectedLocation, setSelectedLocation] = useState(null);
    const [anchorEl, setAnchorEl] = useState(null);
    const [randomLocation, setRandomLocation] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/locations")
            .then(response => response.json())
            .then(data => {
                setLocations(data);
                setRandomLocation(data[Math.floor(Math.random() * data.length)]); // Select a random location
            })
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    useEffect(() => {
        if (randomLocation) {
            handleLocationClick(randomLocation); // Fetch predictions for the random location
        }
    }, [randomLocation]);

    const handleSearchChange = (event) => {
        const query = event.target.value.toLowerCase();
        const matches = locations.filter(location => location.toLowerCase().includes(query));
        setAnchorEl(event.currentTarget);
        // Handle your filtered locations logic here
    };

    const handleLocationClick = (location) => {
        setSelectedLocation(location);
        setLoading(true);
        setAnchorEl(null);

        fetch(`http://127.0.0.1:8000/predict`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ location }),
        })
            .then(response => response.json())
            .then(data => {
                let adjustedAccidents = data.map(d => {
                    let accidents = d.total_accidents;
                    if (d.rainfall > 70) accidents += 2;
                    else if (d.rainfall > 65) accidents += 1;
                    return { ...d, total_accidents: accidents };
                });

                setPredictions(adjustedAccidents);
                setRainfallWarning(adjustedAccidents[0].rainfall > 50 ? 'High rainfall expected, take precautions!' : '');
                setAccidentWarning(adjustedAccidents.some(d => d.total_accidents > 2) ? 'High accident risk due to weather conditions!' : '');
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching prediction:", error);
                setLoading(false);
            });
    };

    const handleDropdownClose = () => {
        setAnchorEl(null);
    };

    const getBackgroundStyles = (humidity) => {
        if (humidity > 70) {
            return {
                className: 'rainy-background', // Assign a class for rainy background
                icon: <RainyIcon fontSize="large" />
            };
        }
        return {
            className: 'sunny-background', // Assign a class for sunny background
            icon: <WbSunnyIcon fontSize="large" />
        };
    };

    const backgroundStyles = getBackgroundStyles(predictions[0]?.humidity_9am || 50);

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={backgroundStyles.className} // Use the background class
            style={{
                height: "100vh",
                color: "white",
                padding: "20px"
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={3}>
                    {/* Left Section */}
                    <Grid item xs={4}>
                        <Typography variant="h4" gutterBottom style={{ fontWeight: "bold", color: "#BBDEFB" }}>
                            Weather Prediction
                        </Typography>

                        <TextField
                            fullWidth
                            label="Search Location"
                            variant="outlined"
                            onChange={handleSearchChange}
                            style={{ marginBottom: "20px", backgroundColor: "#1C2331", color: "white" }}
                            InputLabelProps={{ style: { color: '#BBDEFB' } }}
                        />

                        {loading && <CircularProgress color="inherit" />}

                        <Box mt={2}>
                            {rainfallWarning && <Alert severity="warning">{rainfallWarning}</Alert>}
                            {accidentWarning && <Alert severity="warning">{accidentWarning}</Alert>}
                        </Box>
                    </Grid>

                    {/* Right Section */}
                    <Grid item xs={8}>
                        {selectedLocation && predictions.length > 0 && (
                            <motion.div initial={{ y: -10 }} animate={{ y: 0 }} transition={{ duration: 0.5 }}>
                                <Card style={{ backgroundColor: "rgba(255, 255, 255, 0.2)", padding: "20px", color: "#ECEFF1" }}>
                                    <CardContent>
                                        <Typography variant="h3" align="center">{predictions[0].max_temp}Â°C</Typography>
                                        <Typography variant="h6" align="center">{selectedLocation}</Typography>
                                        {backgroundStyles.icon}
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}
                    </Grid>
                </Grid>
            </Container>
        </motion.div>
    );
}

export default Homepage;
